mapper.update:
UPDATE adopt
SET is_del=#{delDate}
WHERE adopt_id = #{adoptId};

UPDATE adopt
SET is_del=#{delDate}
WHERE adopt_id IN

    #{adoptId}

mapper.select:
select adopt_id, adopt_code,a.pet_id, a.client_id, adopt_money, adopt_info,in_adopt,a.create_time,b.pet_name,c.client_name FROM adopt as a,pet as b,client as c WHERE a.is_del = '0' AND a.pet_id = b.pet_id AND a.client_id = c.client_id
and adopt_code like #{code}
order by create_time desc limit #{pu.begin},#{pu.size};

select count(*)
from adopt
where is_del = '0'
  and in_adopt = 2
  and pet_id = #{petId}
  and client_id = #{clientId}
